class Solution {
public:
    long long minCost(vector<int>& nums, vector<int>& cost) {
        int n = nums.size();
        vector<pair<int, int>> v;
        long long totalCost = 0;

        for(int i = 0; i < n; i++){
            v.push_back({nums[i], cost[i]});
             totalCost += cost[i];
        }
        sort(v.begin(), v.end()); //sort according to num


        long long mid = (totalCost+1)/2;
        long long target = -1; 
        long long currSum = 0; //sum of cost

        for(int i = 0; i < n; i++){
            currSum += v[i].second;
            if(currSum >= mid){
                target = v[i].first; //target is from num array
                break;
            }
        }

        long long ans = 0;
        for(int i = 0; i < n; i++){
            ans += abs((long long)nums[i] - (long long)target) * (long long) cost[i];
        }

        return ans;
    }
};
