class Solution {
public:
    long long find(vector<int>& nums, vector<int>& cost, int x) {
        long long res = 0;
        for (int i=0; i<nums.size(); ++i) res += 1L * abs(nums[i] - x) * cost[i]; //Calculating cost for every element in nums if x is the median using loop
        return res;
    }
    long long minCost(vector<int>& nums, vector<int>& cost) {
        long long total=0;
        long long sum=0;
        long long median;
        vector<pair<int,int>> v;
        for(int i=0; i<nums.size(); i++) {
            v.push_back({nums[i], cost[i]}); // make pair of{nums, cost to sort them}
        }
        sort(v.begin(),v.end()); //sorting them
        for(int i=0; i<cost.size(); i++) {
            sum += v[i].second; //Grasping totoal count/cost as we want to find the count/2 which is median
        }
        int i = 0;
        while(total < (sum+1)/2 && i < nums.size()) { //Simple loop until we reach median
            total += v[i].second;
            median = v[i].first;
            i++;
        }
        return find(nums, cost, median); //calling function with calculated median
    }
};
```

