# Code
```
class Solution {
public:
    long long minCost(vector<int>& nums, vector<int>& cost) {
        int n = nums.size();
        vector<pair<int,int>> arr;
        for(int i=0 ; i<n ; i++) arr.push_back({nums[i],cost[i]});
        sort(begin(arr),end(arr)); // sort the array to calculate median

        long long sum = accumulate(begin(cost),end(cost),0LL);

        long long tot=0;
        int i = 0;
        int median = 0;
        // calculating median
        while(tot < (sum+1)/2 && i < n) {
            tot += arr[i].second;
            median = arr[i].first;
            i++;
        }

        long long ans = 0;
        for(int i=0 ; i<n ; i++) {
            ans += 1ll*abs(arr[i].first-median)*arr[i].second;
        }
        return ans;
    }
};
