class Solution {
public:
    long long minCost(vector<int>& nums, vector<int>& cost) {
        int n=nums.size();
        vector<pair<int,int>> vp;
        long long sum=0;
        for(int i=0;i<n;i++){
            sum+=cost[i];
            vp.push_back({nums[i],cost[i]});
        }
        long long  median=(sum+1)/2;
        sort(vp. begin(),vp.end());
        vector<long long>pre(n);
        pre[0]=vp[0].second;
        for(int i=1;i<n;i++){
            pre[i]=pre[i-1]+vp[i].second;
        }
        int ind=lower_bound(pre.begin(),pre.end(),median)-pre.begin();
        long long ans=0;
        for(int i=0;i<n;i++){
            ans+=abs(vp[i].first-vp[ind].first)*1LL*vp[i].second;
        }
        return ans;
    }
};
