solutions.content:
* ***Using Binary Search***

* ***Time Complexity :- O(N * log(maxi - mini))***

* ***Space Complexity :- O(1)***

```
class Solution {
public:

    // function for finding cost to make all the elements equal to val

    long long find_cost(vector<int>& nums, vector<int>& cost, int val)
    {
        int n = nums.size();

        long long curr_cost = 0;

        for(int i = 0; i < n; i++)
        {
            curr_cost += (long long) abs(nums[i] - val) * (long long) cost[i];
        }

        return curr_cost;
    }

    long long minCost(vector<int>& nums, vector<int>& cost) {

        int n = nums.size();

        // apply binary search on possible ans [mini, maxi] and take minimum

        int low = *min_element(nums.begin(), nums.end());

        int high = *max_element(nums.begin(), nums.end());

        long long ans = 0;

        while(low < high)
        {
            int mid = low + (high - low) / 2;

            // find minimum cost for mid

            long long ans1 = find_cost(nums, cost, mid);

            // find minimum cost for mid + 1

            long long ans2 = find_cost(nums, cost, mid + 1);

            // take the minimum of ans1 and ans2

            ans = min(ans1, ans2);

            // move pointer

            if(ans1 >= ans2)
            {
                low = mid + 1;
            }
            else
            {
                high = mid;
            }
        }

        return ans;
    }
};
```

