class Solution:
    def minCost(self, nums: List[int], cost: List[int]) -> int:
        l, r = min(nums), max(nums)+1 
        @cache
        def h(n):
            total = 0
            for i in range(len(nums)):
                total += abs(nums[i] - n) * cost[i]
            # print(n, total)
            return total 
        while l < r:
            m = (l + r)//2
            if h(m) > h(m-1):
                r = m
            else:
                l = m +1  
        return h(l-1)
