        for(i = n-1;i>=candidates&& i >= n-candidates;i--){
            e.push(costs[i]);
        }

        start = candidates;
        end = n- candidates-1;

        while(k > 0){
            if(s.empty() == false && e.empty() == false)
            {
                if(s.top() <= e.top())
                {
                    ans= ans + s.top();
                    s.pop();

                    if(start <= end){
                        s.push(costs[start]);
                        start++;
                    }
                }
                else
                {
                    ans=ans+e.top();
                e.pop();

                if(start<=end){
                    e.push(costs[end]);
                    end--;
                }
              }
            }
            else if(s.empty() == false){
                ans = ans + s.top();
                s.pop();
            }
            else if(e.empty() == false)
            {
                ans = ans + e.top();
                e.pop();
            }
               k--; 
            }
        return ans;
        }

};
