class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int candidates) {
        multiset<long long>first,last;
        int n = costs.size();
        for(int i=0;i<candidates;i++){
            first.insert(costs[i]);
        }
        for(int i=max(candidates,n-candidates);i<n;i++){
                last.insert(costs[i]);
        }
        int l = candidates;
        int r = n-1-candidates;
        long long ans =0;
        for(int i=0;i<k;i++){
           long long front = INT_MAX,back = INT_MAX;
            if(first.size())front = *first.begin();
            if(last.size()) back = *last.begin();
            if(front<=back){
                first.erase(first.begin());
                ans+=front;
                if(l<=r){
                    first.insert(costs[l]);
                    l++;
                }
            }else{
                ans+=back;
                last.erase(last.begin());
                if(r>=l){
                    last.insert(costs[r]);
                    r--;
                }
            }
        }
        return ans;
    }
};
