class Solution {
    public long totalCost(int[] costs, int k, int c) {
        long sum=0;int n=costs.length;
        Queue<Integer> q1=new PriorityQueue<>();
        Queue<Integer> q2=new PriorityQueue<>();
        int start=c;
        for(int i=0;i<c;i++){
            q1.add(costs[i]);
        }
        int end=Math.max(n-c,c)-1;
        for(int i=n-1;i>=end+1;i--){
            q2.add(costs[i]);
        }
        while(k-->0){
            if((q1.isEmpty()?(int)1e9:q1.peek())<=(q2.isEmpty()?(int)1e9:q2.peek())){
                sum+=q1.poll();
                if(start<=end){
                    q1.add(costs[start]);

                    start++;
                }
            }
            else{
                sum+=q2.poll();
                if(start<=end){

                    q2.add(costs[end]);
                    end--;
                }
            }
        }
        return sum;
    }
}
