class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int candidates) {
         priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> pq;
         int l = candidates , r = ((int)costs.size() - 1) - candidates;
         for(int i=0; i<candidates; i++) pq.push({costs[i],0});
         for(int i=costs.size()-1; i>=max(candidates,r+1); i--) pq.push({costs[i],1});
         long long ans = 0;
         while(k>0) {
              ans+=pq.top().first;
              k--;
              if(l>r) {
                  pq.pop();   
                  continue;
              }
              if(pq.top().second) {
                  pq.pop();
                  pq.push({costs[r],1});      // if smallest element belongs to end part , take the previous r and & remove the current element
                  r--;
              }else {
                  pq.pop();
                  pq.push({costs[l],0});     // if smallest element belongs to start part , take the next l and & remove the current element
                  l++;
              }
         }
        return ans;
    }
};
