```
class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int n) {
        // The function takes three parameters:
        // - costs: a vector of integers representing the costs of different items
        // - k: an integer representing the number of items to be selected
        // - n: an integer representing the number of items at both ends to be ignored

        int len = costs.size(); // Get the size of the costs vector

        if (len <= 2 * n) {
            // If the size of the costs vector is less than or equal to 2 times n,
            // it means there are not enough items to ignore on both ends.
            // In this case, sort the costs vector and return the sum of the first k elements.
            sort(costs.begin(), costs.end());
            return accumulate(costs.begin(), costs.begin() + k, 0LL);
        }

        // Create two priority queues:
        // - pq0: to store the n smallest elements from the beginning of the costs vector
        // - pq1: to store the n smallest elements from the end of the costs vector
        priority_queue<int, vector<int>, greater<int>> 
        pq0(costs.begin(), costs.begin() + n),
        pq1(costs.end() - n, costs.end());

        long long sum = 0; // Variable to store the sum of selected elements
        for (int l = n, r = len - 1 - n; k > 0; k--) {
            // Iterate until k items have been selected

            int x = pq0.empty() ? INT_MAX : pq0.top(); // Get the smallest element from pq0
            int y = pq1.empty() ? INT_MAX : pq1.top(); // Get the smallest element from pq1

            if (x <= y) {
                // If the smallest element from pq0 is smaller than or equal to the smallest element from pq1,
                // select the smallest element from pq0 and update the sum.
                sum += x;
                pq0.pop(); // Remove the selected element from pq0
                if (l <= r)
                    pq0.push(costs[l++]); // Add the next element from the ignored region to pq0
            } else {
                // If the smallest element from pq1 is smaller than the smallest element from pq0,
                // select the smallest element from pq1 and update the sum.
                sum += y;
                pq1.pop(); // Remove the selected element from pq1
                if (l <= r)
                    pq1.push(costs[r--]); // Add the next element from the ignored region to pq1
            }
        }

        return sum; // Return the total cost
    }
};

```

