class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int candidates) {
        priority_queue<int, vector<int>, greater<int>> pq1, pq2;
        long long cost = 0;

        int i=0;
        int j = costs.size() - 1;
        while(k--)
        {
            while(pq1.size() < candidates && i<=j)
                pq1.push(costs[i++]);

            while(pq2.size() < candidates && i<=j)
                pq2.push(costs[j--]);

            int minLeft = pq1.size()>0 ? pq1.top() : INT_MAX;
            int minRight = pq2.size()>0 ? pq2.top() : INT_MAX;

            if(minLeft <= minRight)
            {
                cost+=minLeft;
                pq1.pop();
            }
            else{
                cost+=minRight;
                pq2.pop();
            }
        }
        return cost;
    }
};
