//writing comparation class for our priority queue
class compar{
     public:
     bool operator()(pair<int,int> &a , pair<int,int> &b)
      {
              if(a.first == b.first)
                {
                     return a.second > b.second;
                }
              else 
                     return a.first > b.first;
      }
};
class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int candidates) {
          long long int result = 0;
          int n = costs.size();

        //storing element and index both so to deal with same valued element
          priority_queue<pair<int,int> ,vector<pair<int,int>> , compar> pq1 , pq2;


          for(int i = 0 ; i < candidates ; i++)
          {   

              if(costs[i]==INT_MAX || costs[n-i-1]==INT_MAX)break;
              pq1.push({costs[i] , i});
              pq2.push({costs[n-1-i] , n - i - 1});

            //changing cost of ith worker so that the are inserted in priority queue , to reduce time complexity
              costs[i] = INT_MAX;
              costs[n-1-i] = INT_MAX;
          }


          int last = n - 1 - candidates; //for further we take out from pq2 we have to insert in it from last;

          int first = candidates; //same for pq1


          for(int i = 0 ; i < k; i++)
          {     
                pair<int,int> temp1; 
                pair<int,int> temp2;

                //if no element in any queue we set it to maximum
                if(pq1.size()){
                    temp1 = pq1.top();
                }
                else{
                    temp1 = {INT_MAX,INT_MAX};
                }
                if(pq2.size())
                {
                    temp2 = pq2.top();
                }
                else{
                    temp2 = {INT_MAX,INT_MAX};
                }



                if(temp2.first < temp1.first)
                {
                    pq2.pop();
                    result += temp2.first;
                    if(last>=0 && costs[last]!=INT_MAX){
                        pq2.push({costs[last] , last});
                        costs[last] = INT_MAX;
                    }
                    last--;
                }
                else
                {    
                    //to check if we encounter same worker
                    if(temp1.first == temp2.first && temp1.second == temp2.second){
                        pq2.pop();
                        if(last>=0 && costs[last]!=INT_MAX){
                            pq2.push({costs[last] , last});
                            costs[last] = INT_MAX;
                            last--;
                        }
                    }
                    pq1.pop();
                    result += temp1.first;
                    if(first < n && costs[first]!=INT_MAX){
                        pq1.push({costs[first] , first});
                        costs[first] = INT_MAX;
                    }
                    first++;
                }
          }

          return result;
    }
};
