            }
        }

        var l = candidates
        var r = n - 1 - candidates
        var cost = 0

        for _ in 0..<k {
            let a = queue.dequeue()!
            cost += a.0
            if l > r { continue }

            if a.1 == 0 {
                queue.enqueue((costs[l], 0))
                l += 1
            } else {
                queue.enqueue((costs[r], 1))
                r -= 1
            }
        }
        return cost
    }
}

struct PriorityQueue<T> {
    private var heap: Heap<T>

    init(sort: @escaping (T, T) -> Bool) {
        heap = Heap(sort: sort)
    }

    var isEmpty: Bool {
        heap.isEmpty
    }

    mutating func enqueue(_ element: T) {
        heap.insert(element)
    }

    mutating func dequeue() -> T? {
        heap.remove()
    }
}

struct Heap<T> {  
    var nodes = [T]()
    private var orderCriteria: (T, T) -> Bool

    init(sort: @escaping (T, T) -> Bool) {
        self.orderCriteria = sort
    }

    init(array: [T], sort: @escaping (T, T) -> Bool) {
        self.orderCriteria = sort
        configureHeap(from: array)
    }

    mutating func configureHeap(from array: [T]) {
        nodes = array
        for i in stride(from: (nodes.count/2-1), through: 0, by: -1) {
            shiftDown(i)
        }
    }

    var isEmpty: Bool { 
        nodes.isEmpty
    }

    var count: Int {
        nodes.count
    }

    @inline(__always) 
    func parentIndex(ofIndex i: Int) -> Int {
        (i - 1) / 2
    }

    @inline(__always) 
    func leftChildIndex(ofIndex i: Int) -> Int {
        return 2 * i + 1
    }

    @inline(__always) 
    func rightChildIndex(ofIndex i: Int) -> Int {
        return 2 * i + 2
    }

    func peek() -> T? {
        return nodes.first
    }

    mutating func insert(_ value: T) {
        nodes.append(value)
        shiftUp(nodes.count - 1)
    }

    mutating func insert<S: Sequence>(_ sequence: S) where S.Iterator.Element == T {
        for value in sequence { insert(value) }
    }

    mutating func replace(index i: Int, value: T) {
        guard i < nodes.count else { return }    
        remove(at: i)
        insert(value)
    }

    @discardableResult 
    mutating func remove() -> T? {
        guard !nodes.isEmpty else { return nil }      
        if nodes.count == 1 {
            return nodes.removeLast()
        } else {
            let value = nodes[0]
            nodes[0] = nodes.removeLast()
            shiftDown(0)
            return value
        }
    }

    @discardableResult 
    mutating func remove(at index: Int) -> T? {
        guard index < nodes.count else { return nil }
        let size = nodes.count - 1

        if index != size {
            nodes.swapAt(index, size)
            shiftDown(from: index, until: size)
            shiftUp(index)
        }
        return nodes.removeLast()
    }

    mutating func shiftUp(_ index: Int) {
        var childIndex = index
        let child = nodes[childIndex]
        var parentIndex = self.parentIndex(ofIndex: childIndex)

        while childIndex > 0 && orderCriteria(child, nodes[parentIndex]) {
            nodes[childIndex] = nodes[parentIndex]
            childIndex = parentIndex
            parentIndex = self.parentIndex(ofIndex: childIndex)
        }

        nodes[childIndex] = child
    }

    mutating func shiftDown(from index: Int, until endIndex: Int) {
        let leftChildIndex = self.leftChildIndex(ofIndex: index)
        let rightChildIndex = leftChildIndex + 1

        var first = index
        if leftChildIndex < endIndex && orderCriteria(nodes[leftChildIndex], nodes[first]) {
            first = leftChildIndex
        }
        if rightChildIndex < endIndex && orderCriteria(nodes[rightChildIndex], nodes[first]) {
            first = rightChildIndex
        }
        if first == index { return }

        nodes.swapAt(index, first)
        shiftDown(from: first, until: endIndex)
    }

    mutating func shiftDown(_ index: Int) {
        shiftDown(from: index, until: nodes.count)
    }
}

extension Heap where T: Equatable {
    func index(of node: T) -> Int? {
        return nodes.index(where: { $0 == node })
    }

    @discardableResult 
    mutating func remove(node: T) -> T? {
        if let index = index(of: node) {
            return remove(at: index)
        }
        return nil
    }
}

```

