/*
   maintain two min priority queues in order to store first candidates elements and last candidates elements
    then compare the top value from those priority queues and pop from priority queue that have minimum top value 
    add this min top value to answer
	now check if we have any extra element available in the array that is not pushed in any priority till now,
    if yes push it in the priority from which pop operation has just happened
	again repeat above steps k times
    */
    long long totalCost(vector<int>& c, int k, int can) {
        priority_queue<int, vector<int>, greater<int>> pq, pq2;
        int n = c.size();
        int K = -1;
        int l1 = -1, l2 = n;
        for(int i=0;i<can;i++)
        {
            pq.push(c[i]);
            l1++;
            K++;
        }
        for(int j=n-1;j>K && j>=n-can;j--)
        {
            pq2.push(c[j]);
            l2--;
        }

        long long ans = 0;
        while(k--)
        {
            if(pq.empty())
            {
                ans += pq2.top();
                pq2.pop();
                continue;
            }
            else if(pq2.empty())
            {
                ans += pq.top();
                pq.pop();
                continue;
            }

            auto it1 = pq.top();
            auto it2 = pq2.top();

            if(it1 <= it2)
            {
                pq.pop();
                ans += it1;

                if(l2-l1 > 1)
                    pq.push(c[++l1]);
            }
            else
            {
                pq2.pop();
                ans += it2;

                if(l2-l1 > 1)
                    pq2.push(c[--l2]);
            }
        }
        return ans;
    }
