#define ll long long
class Solution {
public:
    ll totalCost(vector<int>& costs, int k, int candidates) {
        ll ans = 0;
        priority_queue<int,vector<int>, greater<int>> pq1, pq2;
        int count = 0, i = 0, j = costs.size()-1;
        while(count < k){
            while(pq1.size() < candidates && i<=j) pq1.push(costs[i++]);
            while(pq2.size() < candidates && j>=i) pq2.push(costs[j--]);
            int x = (pq1.size()) ? pq1.top():INT_MAX;
            int y = (pq2.size()) ? pq2.top():INT_MAX;
            if(x <= y){
                ans += x;
                pq1.pop();
            }else{
                ans += y;
                pq2.pop();
            }
            count++;
        }
        return ans;
    }
};
