 dry run this code for better understanding and think about corner cases when k is greater than two times of candidates. 


# Code
```

class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int candidates) {
        int n = costs.size();
        long long ans = 0;
        priority_queue<int,vector<int>,greater<int> >pq1,pq2;

        for(int i=0;i<candidates;i++){
            pq1.push(costs[i]);
        }
        for(int i=n-1;i>=max(n-candidates,candidates);i--){
            pq2.push(costs[i]);
        }
        int end = candidates , start = n-candidates-1;

        while(k--){
            int a=INT_MAX,b=INT_MAX;
            if(!pq1.empty())
             a = pq1.top();
            if(!pq2.empty())
             b = pq2.top();

            if(a<=b or b==INT_MAX){
                ans += (long long)a;
                pq1.pop();
                if(end<=start)
                pq1.push(costs[end]),end++;
            }else {
                  ans += (long long)b;
                pq2.pop();
                if(end<=start)
                pq2.push(costs[start]),start--;

            }
        }
         return ans;       

    }
};
```

