class Solution {
public:

    long long totalCost(vector<int>& costs, int k, int candidates) {

        int n = costs.size();
        priority_queue<pair<int,int>, vector<pair<int,int>>, greater<pair<int,int>>> pq;

        for(int i=0; i<candidates && i<(n+1)/2; i++){

            pq.push({costs[i],i});

            if(i<n-i-1){
                pq.push({costs[n-i-1],n-i-1});
            }
        }
        long long ans = 0;
        int first = candidates, second = n-candidates-1;

        while(k--){
            pair<int,int> temp = pq.top();
            pq.pop();

            ans += temp.first;
            if(first<=second){
                if(temp.second<first){
                    pq.push({costs[first],first++});
                }else{
                    pq.push({costs[second],second--});
                }
            }
        }
        return ans;
    }
};
