class Solution {
public:
    long long totalCost(vector<int> &costs, int k, int candidates)
{
    int n = costs.size();
    long long ans = 0;

    if (((2 * candidates) + k - 1) >= n)
    {
        priority_queue<int, vector<int>, greater<int>> pq;
        for (int i = 0; i < n; i++)
        {
            pq.push(costs[i]);
        }

        while (k--)
        {
            ans += pq.top();
            pq.pop();
        }
    }
    else
    {
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;

        for (int i = 0; i < candidates; i++)
        {
            pq.push({costs[i], i});
        }

        int fr = candidates;

        for (int i = n - 1; i >= n - candidates; i--)
        {
            pq.push({costs[i], i});
        }

        int sec = n - candidates - 1;

        while (k--)
        {
            int x = pq.top().first;
            int index = pq.top().second;
            pq.pop();

            ans += x;
            if (index < fr)
            {
                pq.push({costs[fr], fr});
                fr++;
            }
            else
            {
                pq.push({costs[sec], sec});
                sec--;
            }
        }
    }

    return ans;
}
};
