import heapq
from dataclasses import dataclass

@dataclass
class Candidate:
    ind: int
    cost: int

    def __lt__(self, other):
        return (self.cost, self.ind) < (other.cost, other.ind)


class CandidatesPool:
    def __init__(self, costs: List[int], size: int):
        self.costs = costs
        self.heap = []
        self.next_left_candidate_ind = 0
        self.next_right_candidate_ind = len(costs) - 1

        cur_size = 0
        while True:
            if cur_size == size:
                break
            self.try_add_left_candidate()
            cur_size += 1

            if cur_size == size:
                break
            self.try_add_right_candidate()
            cur_size += 1

    def extract_best_candidate(self):
        candidate = heapq.heappop(self.heap)
        self._replace_candidate(candidate)
        return candidate

    def try_add_left_candidate(self):
        if self.has_new_candidates:
            heapq.heappush(self.heap, self.next_left_candidate)
            self.next_left_candidate_ind += 1

    def try_add_right_candidate(self):
        if self.has_new_candidates:
            heapq.heappush(self.heap, self.next_right_candidate)
            self.next_right_candidate_ind -= 1

    @property
    def next_left_candidate(self):
        return Candidate(self.next_left_candidate_ind, self.costs[self.next_left_candidate_ind])

    @property
    def next_right_candidate(self):
        return Candidate(self.next_right_candidate_ind, self.costs[self.next_right_candidate_ind])

    @property
    def has_new_candidates(self):
        return self.next_left_candidate_ind <= self.next_right_candidate_ind

    def _replace_candidate(self, candidate: Candidate):
        if candidate.ind < self.next_left_candidate_ind:
            self.try_add_left_candidate()
        else:
            self.try_add_right_candidate()


class Solution:
    def totalCost(self, costs: List[int], k: int, candidates: int) -> int:
        pool = CandidatesPool(costs, size=2*candidates)
        total_cost = 0
        for _ in range(k):
            candidate = pool.extract_best_candidate()
            total_cost += candidate.cost
        return total_cost


```

