class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int candidates) {
        priority_queue<int, vector<int>, greater<int>> left, right;

        int i=0, j=costs.size()-1;
        while(i < candidates){
            left.push(costs[i]);
            costs[i] = INT_MAX;
            i++;
        }
        while(costs.size()-1-j < candidates){
            right.push(costs[j]);
            costs[j] = INT_MAX;
            j--;
        }

        long long ans = 0;
        while(k--){
            if(!right.size() || (left.size() && left.top() <= right.top())){
                int num = left.top();
                left.pop();

                ans += num;

                if(i < costs.size()){
                    left.push(costs[i]);
                    costs[i] = INT_MAX;
                    i++;
                }
            }
            else{
                int num = right.top();
                right.pop();

                ans += num;

                if(j >= 0){
                    right.push(costs[j]);
                    costs[j] = INT_MAX;
                    j--;
                }
            }
        }

        return ans;
    }
};
```

