class Drive{
    public :
    int cost;
    int flag;
    Drive(int cost,bool flag)
    {
        this->cost = cost;
        this->flag = flag;
    }

};

class myComparator{
public:
    int operator() (const Drive* p1, const Drive* p2)
    {
        if( p1->cost > p2->cost)
            return true;
        if(p1->cost == p2->cost)
        {
            if(p1->flag > p2->flag)
                return true;
            else if(p1->flag < p2->flag)
                return false; 
        }
        return false;
    }
};
class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int candidates) {
        int n = costs.size();

        priority_queue<Drive* ,vector<Drive*>, myComparator>pq;
        int candi = candidates;
        int i = 0;
        for(i=0;i<candi;i++)
        {
            Drive *dnode = new Drive(costs[i],0);
            pq.push(dnode);
        }
        int start = i-1;
        i = n-1;
        while(candi-- != 0)
        {
            if(i == start)
                break;
            Drive *dnode = new Drive(costs[i],1);
            pq.push(dnode);
            i--;
        }
        int end = i+1;
        long long ans = 0;
        while(k-- != 0 )
        {
            Drive *dnode = pq.top();
            pq.pop();
            ans += (long long)dnode->cost;

            bool flag = dnode->flag;

            if(flag == 1)
            {
                end--;
                if(end <=  start)
                    continue;
                Drive *dnode1 = new Drive(costs[end],1);
                pq.push(dnode1);
            }
            else
            {
                start++;
                if(start >= end)
                    continue;
                Drive *dnode1 = new Drive(costs[start],0);
                pq.push(dnode1);
            }
        }
        return ans;  

    }
};
```

