class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int cand) {
        long long ans=0;
        if(costs.size()<=2*cand)
        {
            sort(costs.begin(),costs.end());
            for(int i=0;i<k;i++) ans+=costs[i];
        }
        else
        {
            priority_queue<vector<int>,vector<vector<int>>,greater<vector<int>>>pq;
            int i=0,n=costs.size(),j=costs.size()-cand;
            for(i=0;i<cand;i++)
            {
                pq.push({costs[i],i});
                pq.push({costs[n-i-1],n-i-1});
            }
            i--;
            while(k && i<j)
            {
                auto v=pq.top(); pq.pop();
                long long res=v[0];
                int ind=v[1];
                ans+=res;
                k--;
                if(ind<=i) 
                {
                    i++;
                    if(i<j)
                    pq.push({costs[i],i});
                }
                else
                {
                    j--;
                    if(i<j)
                    pq.push({costs[j],j});
                }
            }
            while(k)
            {
                ans+=pq.top()[0];
                pq.pop();
                k--;
            }
        }
        return ans;
    }
};

