impl Solution {
    pub fn total_cost(costs: Vec<i32>, k: i32, candidates: i32) -> i64 {
        use std::collections::BinaryHeap;
        use std::cmp::Reverse;

        let mut heap = BinaryHeap::new();
        let mut i = 0;
        let mut j = costs.len() - 1;
        for _ in 0..candidates {
            if i >= costs.len() {
                break;
            }
            heap.push((Reverse(costs[i]), Reverse(i), 0));
            i += 1;
        }
        for _ in 0..candidates {
            if j <= i {
                break;
            }
            heap.push((Reverse(costs[j]), Reverse(j), 1));
            j -= 1;
        }

        let mut ret = 0;
        for _ in 0..k {
            if let Some((Reverse(n), _, dir)) = heap.pop() {
                ret += n as i64;
                if i <= j {
                    if dir == 0 {
                        heap.push((Reverse(costs[i]), Reverse(i), 0));
                        i += 1;
                    } else {
                        heap.push((Reverse(costs[j]), Reverse(j), 1));
                        j -= 1;
                    }
                }
            }
        }
        ret
    }
}
