                        swap( &heap[f], &heap[son2] ) ;
                        f = son2 ;
                        continue ;
                    }
                    else
                        break ;                    
                }
            }
            else if(son1 < N){
                if(heap[f]->val > heap[son1]->val){
                    swap( &heap[f], &heap[son1] ) ;
                    f = son1 ;
