class Pair{
    int cost;
    int index;
    Pair(int cost, int index){
        this.cost = cost;
        this.index = index;
    }
}
class Solution {
    public long totalCost(int[] costs, int k, int candidates) {
        PriorityQueue<Pair> pq = new PriorityQueue<>((a,b)->{   
                                                                if(a.cost == b.cost) return a.index-b.index;
                                                                return a.cost - b.cost;
                                                            }
                                                    );
        long total = 0; 
        int start = 0; 
        int n = costs.length;
        int end = n-1;
        while(start<Math.min(n,candidates)){
            pq.add(new Pair(costs[start],start));
            start++;
        }

        while(end>=Math.max(start,n-candidates)){
            pq.add(new Pair(costs[end],end));
            end--;
        }
        while(k-->0){
            Pair p = pq.poll();
            total += p.cost;
            int index = p.index;
            if(index>end && index<=n-1 && end>=start){
                pq.add(new Pair(costs[end],end));
                end --;
            }
            else if(index>=0 && index<start && start<=end){
                pq.add(new Pair(costs[start],start));
                start++;
            }
        }
        return total;
    }
}
