            if(priorityQueue1.peek()<=priorityQueue2.peek()){
                minCost+=priorityQueue1.poll();
                priorityQueue1.add(deque.getFirst());
                deque.removeFirst();
            }
            else{
                minCost+=priorityQueue2.poll();
                priorityQueue2.add(deque.getLast());
                deque.removeLast();
            }
            k--;
        }
    while(k>0){
             if(!priorityQueue1.isEmpty() && priorityQueue1.peek()<=priorityQueue2.peek()){ minCost+=priorityQueue1.poll();                                                  }
            else {
                minCost+=priorityQueue2.poll();
            }
            k--;
        }
        return minCost;  
