class Solution {
    public long totalCost(int[] costs, int k, int candidates) {
        int len = costs.length, i = 0, j = len-1;
        long ans = 0;
        PriorityQueue<Integer> l = new PriorityQueue<>();
        PriorityQueue<Integer> r = new PriorityQueue<>();

        while(i<candidates) l.add(costs[i++]);

        while(j>=len-candidates && j>=i) r.add(costs[j--]);

        while(k-- > 0){
            ans += (!r.isEmpty() && !l.isEmpty()) ? (l.peek() <= r.peek()) ? l.poll() : r.poll() : (r.isEmpty() ? l.poll() : r.poll());
            if(l.size() < candidates && i<=j){
                l.add(costs[i++]);
            }
            else if(r.size() < candidates && j>=i){
                r.add(costs[j--]);
            }
        }
        return ans;
    }
}
