class Solution {
public:
    long long totalCost(vector<int>& cost, int need, int candidates) {
        long ans = 0;
        int n = cost.size();

        int left = 0, right = n - 1;
        priority_queue<int, vector<int>, greater<int>> lq, rq;

        // pushing the starting candidates elements in the left queue
        for (int i = 0; i < candidates; i++) 
            lq.push(cost[left++]); 

        // pushing the last candidates elements in the right queue and also making sure that we don't cross the left pointer
        for (int i = 0; i < min(candidates, n - candidates); i++) 
            rq.push(cost[right--]);


        for (int i = 0; i < need; i++) {
            int lTop = lq.empty() ? INT_MAX : lq.top();
            int rTop = rq.empty() ? INT_MAX : rq.top();

            if (lTop <= rTop) {
                ans += lTop;
                lq.pop();

                if (left <= right) 
                    lq.push(cost[left++]);
            }
            else {
                ans += rTop;
                rq.pop();

                if (left <= right) 
                    rq.push(cost[right--]);
            }
        }

        return ans;
    }
};
