	pq2.push(costs[r --]);

// k sessions
while (k --)
{
	// under the following circumstances, 
	// we pick the one with lowest cost from the second min-heap.
	if (pq1.empty() || 
	   (pq1.size() && pq2.size() && pq2.top() < pq1.top()))
	{
		ret += pq2.top();
		pq2.pop();

		// add one more worker to the heap, if there are any.
		if (l <= r)
			pq2.push(costs[r --]);
	}
	// similarly
	else 
	{
		ret += pq1.top();
		pq1.pop();

		if (l <= r)
			pq1.push(costs[l ++]);
	}
}

return ret;
