class Solution {
    List<List<Integer>>ans= new ArrayList<>();
    public void swap(int i,int j,int[]nums){
        int k=nums[i];
        nums[i]=nums[j];
        nums[j]=k;
    }

    public void fun(int k, int[]nums){
        if(k==nums.length){
            List<Integer>a= new ArrayList<>();
        for(int x:nums)a.add(x);
            ans.add(a);
            return;
        }
