object Solution {
    def permuteUnique(nums: Array[Int]): List[List[Int]] = {
        permute(nums.toList)
    }

    def permute(nums: List[Int]): List[List[Int]] = {
        nums match {
            case List() => List(List())
            case _ => for {
                x <- nums.distinct
                perm <- permute(removeElem(nums, x))
            } yield x :: perm
        } 
    }

    def removeElem(nums: List[Int], n: Int): List[Int] = {
        nums match {
            case List() => List()
            case (x :: xs) => if (x == n) { xs } else { x :: removeElem(xs, n) }
        }
    }
}
