        def backtrack(remainder, path):
            remainder = remainder[:]
            path = path[:]
            if len(remainder) == 0:
                result.append(path)
            for i in range(len(remainder)):
                if i > 0 and remainder[i] == remainder[i-1]:
                    continue
                path.append(remainder[i])
                backtrack(remainder[0:i] + remainder[i+1:], path)
                path.pop()
            return

        nums.sort()
        result = []
        backtrack(nums, [])
