    public void swap(int i,int j,int[] nums){
        int temp=nums[i];
        nums[i]=nums[j];
        nums[j]=temp;
    }
    public boolean Permutate(int start,int j,int[] nums){
        for(int i=start;i<j;i++)
            if(nums[i]==nums[j])
                return false;
        return true;
    }
}
