class Solution {
public:
    vector<vector<int>> permuteUnique(vector<int>& nums) {
        vector<vector<int>> res;
        vector<int> ds;
        vector<int> check(nums.size(), 0);

        helper(ds, res, check, nums);
        return res;
    }

    void helper(vector<int> &ds, vector<vector<int>> &res, vector<int> &check, vector<int> &nums){
        if(ds.size() == nums.size() && !(find(res.begin(), res.end(), ds) != res.end())){
            res.push_back(ds);
            return;
        }

        for(int i=0; i<nums.size(); i++){
            if(check[i]==0){        //if unchecked, can be pushed
                ds.push_back(nums[i]);
                check[i] = 1;   //check
                helper(ds, res, check, nums);
                ds.pop_back();
                check[i] = 0;   //uncheck
            }
        }
    }

};
