var permuteUnique = function(nums) {
    nums.sort((a,b) => {
        return a-b
    })

    let temp = []
    let result = []

    function backtracking(nums, temp) {
        if(nums.length === 0) {
            result.push([...temp])
            return
        }

        for(let i=0; i<nums.length; i++) {
            if(i !== 0 && nums[i] == nums[i-1]) continue

            temp.push(nums[i])
            nums.splice(i, 1)
            backtracking(nums, temp)
            nums.splice(i, 0, temp.pop())
        }
    }
    backtracking(nums, temp)
    return result
};

