    public List<List<Integer>> permuteUnique(int[] nums) {
         if(nums.length == 0){
          return new ArrayList<>();
        }
      Arrays.sort(nums);
      boolean visited[] = new boolean[nums.length];
      List<Integer> sans = new ArrayList<>();
      permute_(nums , visited , sans , 0);
      return ans;
    }
  public void permute_(int nums[] , boolean visited[] , List<Integer> sans , int count){

       if(count == nums.length){
         List<Integer> list = new ArrayList<>(sans);
         ans.add(list);
         return;
