       int prev = -(int)1e8;
       for(int i=0 ; i<nums.length ; i++){

            if(!visited[i] && prev!=nums[i]){
              visited[i] = true;
              prev = nums[i];
              sans.add(nums[i]);
              permute_(nums , visited , sans , count + 1);
              sans.remove(sans.size()-1);
              visited[i] = false;
            }

       }

   }
}
