class Solution {
    func permuteUnique(_ nums: [Int]) -> [[Int]] {

        var nums = nums
        nums.sort()

        var result = [[Int]]()
        result.append(nums)

        while true {

            var flipIndex = -1

            var i: Int = nums.count - 2
            while i >= 0 {
                if nums[i + 1] > nums[i] {
                    flipIndex = i
                    break
                }
                i -= 1
            }

            if flipIndex == -1 { break }

            var successorIndex: Int = -1
            var successorValue: Int = 0

            i = flipIndex + 1
            while i < nums.count {
                if nums[i] > nums[flipIndex] {
                    if successorIndex == -1 || nums[i] <= successorValue {
                        successorValue = nums[i]
                        successorIndex = i
                    }
                }
                i += 1
            }

            nums.swapAt(successorIndex, flipIndex)

            var first: Int = flipIndex + 1
            var last: Int = nums.count - 1
            while first < last {
                nums.swapAt(first, last)
                last -= 1
                first += 1
            }
            result.append(nums)
        }
        return result
    }
}
