                else{
                    ListNode search = head;
                    while(search.next != null && search.next.val<current.val){
                        search = search.next;
                    }
                    ListNode tmp = search.next;
                    search.next = current;
                    current.next = tmp;
                }
                current = pre.next;
            }
        }
        return head;
    }
}
