                    //search starting from the head
                    ListNode ptr = head;
                    while(ptr.next!=null && ptr.next.val < curr.val)
                        ptr = ptr.next;
                    ListNode temp = ptr.next;
                    ptr.next = curr;
                    curr.next = temp;
                }
                curr = prev.next;
            } else {
                curr = curr.next;
                prev = prev.next;
            }
        }
       return head; 
    }
}
