 * };
 */
class Solution {
public:
    ListNode* insertionSortList(ListNode* head) {
        // idea is we maintain a prev list & for each node we check in prev list until all elements are smaller then insert the node between the smaller & greater elements.
        if(!head || !head->next)return head;
        ListNode* dummy=new ListNode(INT_MIN);
        ListNode* tail=dummy;
        ListNode* curr=head;
        while(curr){
            ListNode * next=curr->next;
            while(tail->next && tail->next->val<curr->val){
                tail=tail->next;
            }
            curr->next=tail->next;
            tail->next=curr;
            tail=dummy;
            curr=next;
        }
        return dummy->next;

    }
};
```

