/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public ListNode insertionSortList(ListNode head) {
        // if head.size is samller than 2, we can just return head
        if(head==null || head.next ==null) return head;

         // use current to go thourgh the linklist
		 // use temp to store the not-in-order element
		 // use runner to loop over the sorted linklist

        ListNode dummy = new ListNode(-1,head);
        ListNode current = head;
        ListNode temp = null;
        ListNode runner = null;

        while (current.next!=null){
            // if current.next bigger than current, we do not need to change the order
            if (current.next.val >= current.val){
                current = current.next;
            }else{
                runner = dummy;
                temp =   current.next; // save the not in-order element to temp
                current.next = current.next.next;//remove the not in-order element from the list

                // use runner, looping from dummy, to find where to insert temp
                while (runner.next.val <= temp.val){
                    runner = runner.next;
                }
                // find the place to insert, re-build the sorted link-list    
                    temp.next = runner.next;
                    runner.next = temp;
            }
        }
        return dummy.next;
    }
}
