class Solution {
public:
    ListNode* insertionSortList(ListNode* head) {

        //We will store the solution after sentinel..
        ListNode *aSentinel = new ListNode(0);
        ListNode *aPrev = NULL;

        //We pluck nodes out from the original list, one at a time.
        ListNode *aInsert = head;
        ListNode *aNextInsert = NULL;

        //We search through the sorted partial result.
        ListNode *aSearch = NULL;

        while (aInsert) {

            //a begin
            aNextInsert = aInsert->next;

            //Find the spot to insert the node...

            aPrev = aSentinel;
            aSearch = aSentinel->next;
            while (aSearch != NULL && aSearch->val < aInsert->val) {
                aPrev = aSearch;
                aSearch = aSearch->next;
            }

            aPrev->next = aInsert;
            aInsert->next = aSearch;

            //a end
            aInsert = aNextInsert;
        }

        ListNode *aResult = aSentinel->next;
        delete aSentinel;
        return aResult;
    }
};
```

