public ListNode insertionSortList1(ListNode head) {
    if (head == null || head.next == null) {
        return head;
    }
    ListNode p = insertionSortList(head.next);
    if (head.val <= p.val) {  // already sorted
        head.next = p;
        return head;
    }
    ListNode ret = p;
    while (p.next != null && p.next.val < head.val) {
        p = p.next;
    }
    head.next = p.next;
    p.next = head;
    return ret;
}

