        // updating head of input list
        head = head->next;

        while(head){
            // if current node's value is greater than last node of sorted list
            // need to update tail(ptr) 
            if(head->val > ptr->val){
                ptr->next = new ListNode(head->val);
                ptr = ptr->next;
