    class Solution {
    public:
        ListNode *insertionSortListV(ListNode *head) {
            ListNode *i, *j;
            int v;

            for (i = head->next; i; i = i->next) {
                for (j = head; j != i; j = j->next) {
                    if (i->val < j->val)
                        SWAP(i->val, j->val, v);
                }
            }
            return head;
        }

        ListNode *insertionSortListP(ListNode *head) {
            ListNode hd(0);
            ListNode *i, *j, *t, *pi, *pj;

            i = head->next;
            j = head;
            hd.next = head;
            pi = head;
            pj = &hd;
            while (i) {
                while (j != i) {
                    if (i->val < j->val) {
                        SWAP(pi->next, pj->next, t);
                        SWAP(i->next, j->next, t);
                        SWAP(i, j, t);
                    }
                    pj = j;
                    j = j->next;
                }
                j = hd.next;
                pj = &hd;
                pi = i;
                i = i->next;
            }
            return hd.next;

        }
