// Solution: Regular insertion sort algo
// the approach is very straight forward, thinking there are
// 2 slinked list: (1) sorted slinked list, the target one;
// (2) the unsorted one which is the original input
// we will move each node from the original slinked list to 
// the sorted one and insert to the right position.
////////////////////////////////////////////////////////////
class Solution {
private:
    ListNode* dummy = new ListNode(-1);
public:
    ListNode* insertionSortList(ListNode* head) {
        if (!head) return head;

        // [Tips] Using dummy head node in the cases which original head node might be changed.
        ListNode* dummy = new ListNode(-1);    // the dummy head of the sorted slinked list
        ListNode* pre = dummy;                 // previous node of the right inserting position in sorted slinked list
        ListNode* p = head;                    // for interating the unsorted slinked list

        // move the nodes in original slinked list one by one
        while (p) { 
            head = p->next;
            // find the right place in the sorted slinked list
            while (pre->next && pre->next->val < p->val) pre = pre->next;

            // insert the node to sorted slinked list
            p->next = pre->next;
            pre->next = p;
            // reset pointers: pre and p 
            pre = dummy;
            p = head;
        }
        return dummy->next;
    }
};
