class Solution {
    public ListNode insertionSortList(ListNode head) {
        ListNode prev = head;
        ListNode curr = prev.next;

        if(head==null || head.next==null) 
            return head;

        while(curr != null) {
            if(curr.val < prev.val) {
                prev.next = curr.next;
                if(curr.val <= head.val) {
                    curr.next = head;
