    public ListNode insertionSortList(ListNode head) {

    if(head==null) return null;
    if(head.next==null) return head;
     ListNode ptr=head.next;
	 // result is a new list where old elements to be added to new list in sorted way
     ListNode result=new ListNode();
     result.val=head.val;
     ListNode next=result;
     ListNode prev=null;
    while(ptr!=null)
    {
        ListNode temp=new ListNode();
        temp.val=ptr.val;
		/*
		if first element  is smaller than we have to insert at first position in new list( result list)
		eg: old list ->       5->6->7->3->232->45->null
		    result list->     5->6->null 
			 if ptr.val=3 and next.val=5
			 then in result list 3 has to add at beginning of the list
		*/
        if(ptr.val<=next.val)
        {
            temp.next=next;
            next=temp;
            result=temp;
        }
		/*
		else : we have to traverse the list until we find the right postion to insert the  
		*/
        else
        {
            while(ptr.val>next.val && next.next!=null)
            {
                prev=next;
                next=next.next;
            }
			/*
			if result list is already sorted then we have to insert in at last position 
			eg: old list ->   5->10->7->8->12->2->15->null
